'use strict';

//==============================================================================
//  Modules
//==============================================================================

var gameAPI = require('codeNamesOnline/api/gameAPI');

//==============================================================================
//  Exports
//==============================================================================

module.exports = function (app) {

	// API routes

	app.post('/api/createNewGame', function (request, response) {
		gameAPI.CreateNewGame(function (error, gameCode) {
			var result;
			if (error) {
				result = {
					success : false,
					error   : error.message
				};
			}
			else {
				result = {
					success : true,
					data    : gameCode
				};
			}
			response.json(result);
		});
	});

	app.get('/api/game/:gameCode', function (request, response) {
		gameAPI.GetGameData(request.params.gameCode, function (error, gameData) {
			var result;
			if (error) {
				result = {
					success : false,
					error   : error.message
				};
			}
			else {
				result = {
					success : true,
					data    : gameData
				};
			}
			response.json(result);
		});
	});

	// Front end routes

	app.get('*', function (request, response) {
		response.sendFile('index.html', {'root' : './public'});
	});
};
