'use strict';

module.exports = function (name, team) {
	var name = name;
	var team   = team;
	var socket = null;

	// Getters

	this.getName = function () {
		return name;
	};

	this.getTeam = function () {
		return team;
	};

	this.getSocket = function () {
		return socket;
	};

	// Setters

	this.setSocket = function (newSocket) {
		socket = newSocket;
	};
};
