'use strict';

module.exports = function (newName, newTeam) {
	var name   = newName;
	var team   = newTeam;
	var socket = null;

	if (!name || name === '') {
		name = 'JIM';
	}
	if (name === 'J' || name === 'j') {
		name = 'ARSEHAT';
	}
	// Getters

	this.getName = function () {
		return name;
	};

	this.getTeam = function () {
		return team;
	};

	this.getSocket = function () {
		return socket;
	};

	// Setters

	this.setSocket = function (newSocket) {
		socket = newSocket;
	};
};
