'use strict';

//==============================================================================
//  Sockets
//==============================================================================

var sockets = {};

//==============================================================================
//  Exports
//==============================================================================

module.exports.AddSocket = function (socket) {
	sockets[socket.id] = socket;
};

module.exports.RemoveSocket = function (socketId) {
	delete sockets[socketId];
};

module.exports.GetSocket = function (socketId) {
	return sockets[socketId];
};
